    private void IniciarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_IniciarActionPerformed
        PreparedStatement ps;
        ResultSet rs;
        String uname = Userlog.getText();
        String pass = String.valueOf(PassLog.getPassword());

        String query = "SELECT * FROM `usuarios` WHERE `Usuario` =? AND `Pass` =?";

        try {
            ps = MyConnection.getConnectionFinal().prepareStatement(query);

            ps.setString(1, uname);
            ps.setString(2, pass);

            rs = ps.executeQuery();

            if(rs.next())
            {
                Home mf = new Home(uname);
                mf.setVisible(true);
                mf.pack();
                mf.setLocationRelativeTo(null);



                this.dispose();
            }
            else{
                JOptionPane.showMessageDialog(null, "Password o Usuario incorrecto", "Inicio Fallido", 2);
            }
        } catch (SQLException ex) {

        }


    }//GEN-LAST:event_IniciarActionPerformed

